import React, { useState } from 'react';

export function Article(props)
{
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [body, setBody] = useState('');
    const [showInfo, setShowInfo] = useState(false);

    function printInfo(isCalled) {
        if(isCalled)
        setShowInfo(true);
    }

    return(
        <div>
            <form style={{ display: 'flex', flexDirection: 'column', maxWidth: '300px', margin: '0 auto' }}>
                <label style={{ marginBottom: '10px' }}>
                    Title:
                    <input type="text" value={title} onChange={e => setTitle(e.target.value)} style={{ width: '100%', padding: '5px' }} />
                </label>
                <label style={{ marginBottom: '10px' }}>
                    Author:
                    <input type="text" value={author} onChange={e => setAuthor(e.target.value)} style={{ width: '100%', padding: '5px' }} />
                </label>
                <label style={{ marginBottom: '10px' }}>
                    Body:
                    <textarea value={body} onChange={e => setBody(e.target.value)} style={{ width: '100%', padding: '5px' }} />
                </label>
            </form>
            <button onClick={() => printInfo(true)}>Print info</button>
            {showInfo && (
                <div style={{ margin: '20px', padding: '20px', border: '1px solid black', borderRadius: '5px' }}>
                    <h2 style={{ color: 'blue' }}>Title: {title}</h2>
                    <h3 style={{ color: 'green' }}>Author: {author}</h3>
                    <p style={{ color: 'red' }}>Body: {body}</p>
                </div>
            )}
        </div>
    );
}
