import React, { useState } from 'react';

export function Article(props)
{
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [body, setBody] = useState('');
    const [showInfo, setShowInfo] = useState(false);
    let newtitle='';
    let newauthor='';
    let newbody='';
    function printInfo(isCalled) {
        if(isCalled)
        newtitle=title;
        newauthor=author;
        newbody=body;
        setShowInfo(true);
    }

    return(
        <div>
            <form>
                <label>
                    Title:
                    <input type="text" value={title} onChange={e => setTitle(e.target.value)} />
                </label>
                <label>
                    Author:
                    <input type="text" value={author} onChange={e => setAuthor(e.target.value)} />
                </label>
                <label>
                    Body:
                    <textarea value={body} onChange={e => setBody(e.target.value)} />
                </label>
            </form>
            <button onClick={() => printInfo(true)}>Print info</button>
            {showInfo && (
                <div style={{ margin: '20px', padding: '20px', border: '1px solid black', borderRadius: '5px' }}>
                    <h2 style={{ color: 'blue' }}>Title: {newtitle}</h2>
                    <h3 style={{ color: 'green' }}>Author: {newauthor}</h3>
                    <p style={{ color: 'red' }}>Body: {newbody}</p>
                </div>
            )}
        </div>
    );
}
